import { StyleSheet, Text, TouchableOpacity, Image } from 'react-native'
import React from 'react'
import { ColorPalette } from '@app/theme/colors';

/**
 * props type for PageHeader component
 * @property {()=>void} onPress - callback function when logo is pressed
 */
interface Props {
    onPress?: () => void;
}

/**
 * screen header component for main screens
 * @prop {()=>void} onPress - callback function when logo is pressed
 * @returns 
 */
const PageHeader = (props: Props) => {
    return (
        <TouchableOpacity
            style = { styles.container }
            onPress={props.onPress}
        >
            <Image
                style = { styles.logo }
                source = { require("../../../../assets/logo.png")}
            />
            <Text
                style = { styles.title }
            >
                AstroMagnet
            </Text>
        </TouchableOpacity>
    )
}

export default PageHeader

const styles = StyleSheet.create({
    container: {
        flexDirection: "row",
        alignItems: "center",
        paddingLeft: 20,
    },
    logo: {
        paddingLeft: 30,
        width: 80,
        height: 80
    },
    title: {
        paddingLeft: 20,
        fontSize: 35,
        fontWeight: "bold",
        color: ColorPalette.DESATURATED_MAGENTA
    }
})
